mapper.select:
select * from direction 
 where d_no = #{d_no}
  and type = '考研'

select * from direction 
 where d_no = #{d_no}
 and type = '就业'

mapper.update:
update direction set d_no=#{d_no},name=#{name},type=#{type}
,intro_link=#{intro_link}
,vista_link=#{vista_link} 
,update_date=#{update_date}
 where d_no = #{d_no}
